mapper.select:
SELECT ANY_VALUE(research_field) AS research_field, ANY_VALUE(dxnf) AS nf, COUNT(*) AS num
FROM (
SELECT field_id, research_field, b.jgbh, b.jgmc, dxnf
FROM (
SELECT dxnf, gzdw_gf1 AS jgmc
FROM cas_academician_china

qssj IS NULL AND dxnf<=${endYear}

) AS a
INNER JOIN(
SELECT *
FROM relation_field_affiliation

user_id = "${userId}"

AND field_id ${fieldIdsStr}
) AS b
ON a.jgmc=b.jgmc
UNION ALL
SELECT field_id, research_field, b.jgbh, b.jgmc, dxnf
FROM (
SELECT dxnf, gzdw_gf1 AS jgmc
FROM cas_academician_cae_china

qssj IS NULL AND dxnf<=${endYear}

) AS a
INNER JOIN(
SELECT *
FROM relation_field_affiliation

user_id = "${userId}"

AND field_id ${fieldIdsStr}
) AS b
ON a.jgmc=b.jgmc
) AS c
GROUP BY research_field, dxnf;

SELECT ANY_VALUE(jgbh) AS jgbh, ANY_VALUE(jgmc) AS jgmc, ANY_VALUE(dxnf) AS dxnf, COUNT(*) AS num
FROM (
SELECT b.jgbh, b.jgmc, dxnf
FROM (
SELECT dxnf, gzdw_gf1 AS jgmc
FROM cas_academician_china

qssj IS NULL AND dxnf<=${endYear}

) AS a
INNER JOIN(
SELECT DISTINCT jgbh, jgmc
FROM relation_field_affiliation

user_id = "${userId}"

AND jgbh ${jgbhStr}
) AS b
ON a.jgmc=b.jgmc
UNION ALL
SELECT d.jgbh, d.jgmc, dxnf
FROM (
SELECT dxnf, gzdw_gf1 AS jgmc
FROM cas_academician_cae_china

qssj IS NULL AND dxnf<=${endYear}

) AS c
INNER JOIN(
SELECT DISTINCT jgbh, jgmc
FROM relation_field_affiliation

user_id = "${userId}"

AND jgbh ${jgbhStr}
) AS d
ON c.jgmc=d.jgmc
) AS e
GROUP BY jgmc, dxnf;
