user_id = "${userId}"
AND field_id ${arrayStr} ) AS b ON a.gzdw = b.jgmc ) AS c
nf>=${startYear} AND nf<=${endYear}
GROUP BY research_field, jxmc;

SELECT * FROM( SELECT jgmc, jgbh, nf, jxmc, detail FROM ( SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, first_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, first_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, first_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT "何梁何利奖" AS jxmc, nd AS nf, hjxk AS detail, gzdw_std AS gzdw FROM cas_hlhl_award_winner UNION ALL SELECT "未来科学大奖" AS jxmc, hjnf AS nf, hjlb AS detail, hjrdw_std AS gzdw FROM cas_future_prize UNION ALL SELECT "求是杰出科学家奖" AS jxmc, nf, hjxk AS detail, gzdw_std AS gzdw FROM cas_qiushi_scientist_award ) AS a INNER JOIN ( SELECT DISTINCT jgbh, jgmc FROM relation_field_affiliation
user_id = "${userId}"
