jgbh ${arrayStr}
AND nf>=${startYear} AND nf<=${endYear};

SELECT ANY_VALUE(jgmc) AS field, ANY_VALUE(nf) AS year, ANY_VALUE(jxmc) AS unitName,ANY_VALUE(jxmc) AS jxmc , COUNT(*) AS num FROM( SELECT jgmc, jgbh, nf, jxmc, detail FROM ( SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, first_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, second_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT jxlb AS jxmc, hjnf AS nf, xmmc_or_tdmc AS detail, third_wcdw_std AS gzdw FROM cas_china_award_10year_final UNION ALL SELECT "何梁何利奖" AS jxmc, nd AS nf, hjxk AS detail, gzdw_std AS gzdw FROM cas_hlhl_award_winner UNION ALL SELECT "未来科学大奖" AS jxmc, hjnf AS nf, hjlb AS detail, hjrdw_std AS gzdw FROM cas_future_prize UNION ALL SELECT "求是杰出科学家奖" AS jxmc, nf, hjxk AS detail, gzdw_std AS gzdw FROM cas_qiushi_scientist_award ) AS a INNER JOIN ( SELECT DISTINCT jgbh, jgmc FROM relation_field_affiliation
