mapper.select:
SELECT ANY_VALUE(research_field) AS research_field, ANY_VALUE(nf) AS nf, SUM(sq) AS sq, SUM(pz) AS pz, SUM(sx) AS sx FROM ( SELECT b.jgbh, b.jgmc, nf, sq, pz, sx, field_id, research_field FROM stat_patent_new AS a INNER JOIN( SELECT field_id, research_field, jgbh, jgmc FROM relation_field_affiliation
user_id = "${userId}"
) AS b ON a.jgbh=b.jgbh ) AS c
nf>=${startYear} AND nf<=${endYear}
AND field_id ${arrayStr} GROUP BY research_field, nf

SELECT ANY_VALUE(research_field) AS research_field, ANY_VALUE(nf) AS nf, SUM(xz) AS xz
FROM(
SELECT field_id, research_field, jgbh, b.jgmc, nf, xz
FROM (
SELECT jgmc, nf, xz
FROM stat_patent

zllx="PCTPatent"

) AS a
INNER JOIN(
SELECT field_id, research_field, jgbh, jgmc
FROM relation_field_affiliation

user_id = "${userId}"

) AS b
ON a.jgmc=b.jgmc
) AS c

nf>=${startYear} AND nf<=${endYear}

AND field_id ${arrayStr}
GROUP BY research_field, nf;

SELECT jgbh, jgmc, nf, sq, pz, sx FROM ( SELECT b.jgbh, b.jgmc, nf, sq, pz, sx FROM stat_patent_new AS a INNER JOIN( SELECT DISTINCT jgbh, jgmc FROM relation_field_affiliation
user_id = "${userId}"
) AS b ON a.jgbh=b.jgbh ) AS c
nf>=${startYear} AND nf<=${endYear}
AND jgbh ${arrayStr};rt

SELECT jgbh, jgmc, nf, xz FROM( SELECT jgbh, b.jgmc, nf, xz FROM ( SELECT jgmc, nf, xz FROM stat_patent
zllx="PCTPatent"
) AS a INNER JOIN( SELECT DISTINCT jgbh, jgmc FROM relation_field_affiliation
user_id = "${userId}"
) AS b ON a.jgmc=b.jgmc ) AS c
nf>=${startYear} AND nf<=${endYear}
AND jgbh ${arrayStr};rt
