select
from v_ref_org_type
1 = 1
AND JGBH in (
#{item}
)
AND JGBH is not null
AND research_field in (
#{item}
)
AND research_field is not null AND research_field!='NA'
ORDER BY convert(jgmc using gbk) ASC

select DISTINCT research_field AS field from v_ref_org_type
1 = 1
AND JGBH in (
#{item}
)
AND JGBH is not null
AND research_field in (
#{item}
)
AND research_field is not null AND research_field!='NA'
