SELECT g.jgbh, g.jgmc, g.research_field, g.num_qsjc, g.num_wlkx, g.num_hlhl, IFNULL(h.num_gjkj,0) AS num_gjkj FROM ( SELECT e.jgbh, e.jgmc, e.research_field, e.num_qsjc, e.num_wlkx, IFNULL(f.num_hlhl,0) AS num_hlhl FROM ( SELECT c.jgbh, c.jgmc, c.research_field, c.num_qsjc, IFNULL(d.num_wlkx,0) AS num_wlkx FROM ( SELECT a.jgbh, a.jgmc, a.research_field, IFNULL(b.num_qsjc,0) AS num_qsjc FROM ( SELECT jgbh, jgmc, research_field FROM v_ref_org_type
1 = 1
AND research_field IS NOT NULL AND research_field !="NA"
) AS a LEFT JOIN ( SELECT ANY_VALUE(gzdw_std) AS jgmc, COUNT(id) AS num_qsjc FROM v_cas_qiushi_scientist_award
nf >= #{query.startYear} AND nf <= #{query.endYear} AND gzdw_std in()
GROUP BY gzdw_std ) AS b ON a.jgmc = b.jgmc ) AS c LEFT JOIN ( SELECT ANY_VALUE(hjrdw_std) AS jgmc, COUNT(id) AS num_wlkx FROM v_cas_future_prize
hjnf >= #{query.startYear} AND hjnf <= #{query.endYear} AND hjrdw_std in ()
GROUP BY hjrdw_std ) AS d ON c.jgmc = d.jgmc ) AS e LEFT JOIN ( SELECT ANY_VALUE(gzdw_std) AS jgmc, COUNT(id) AS num_hlhl FROM v_cas_hlhl_award_winner
nd >= #{query.startYear} AND nd <= #{query.endYear} AND gzdw_std in ()
GROUP BY gzdw_std ) AS f ON e.jgmc = f.jgmc ) AS g LEFT JOIN ( SELECT ANY_VALUE(jgmc) AS jgmc, COUNT(id) AS num_gjkj FROM ( SELECT id, first_wcdw_std AS jgmc FROM v_cas_china_award_10year_final
first_wcdw_std in () AND hjnf >= #{query.startYear} AND hjnf <= #{query.endYear}
UNION ALL SELECT id, second_wcdw_std AS jgmc FROM v_cas_china_award_10year_final
second_wcdw_std in () AND hjnf >= #{query.startYear} AND hjnf <= #{query.endYear}
UNION ALL SELECT id, third_wcdw_std AS jgmc FROM v_cas_china_award_10year_final
third_wcdw_std in () AND hjnf >= #{query.startYear} AND hjnf <= #{query.endYear}
) AS t GROUP BY jgmc ) AS h ON g.jgmc = h.jgmc

SELECT ANY_VALUE(nf) AS nf, SUM(xzxms) AS xm, ROUND(SUM(xzxm_zjf),2) AS jf FROM v_stat_xdzx AS a, v_ref_org_type AS b
a.jgmc = b.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
GROUP BY nf

SELECT ANY_VALUE(nf) AS nf, SUM(xzxms) AS xm, ROUND(SUM(xzxm_zjf),2) AS jf FROM v_stat_xdzx AS a, v_ref_org_type AS b
a.jgmc = b.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
GROUP BY nf

SELECT ANY_VALUE(jgbh) AS jgbh, ANY_VALUE(a.jgmc) AS jgmc, SUM(xzxms) AS xm, ROUND(SUM(xzxm_zjf),2) AS jf FROM v_stat_xdzx AS a, v_ref_org_type AS b
a.jgmc = b.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
GROUP BY jgbh ORDER BY jgmc

SELECT ANY_VALUE(nf) AS nf, SUM(xzxms) AS xm, ROUND(SUM(xzxm_zjf),2) AS jf FROM v_stat_most_project AS a, v_ref_org_type AS b
a.jgmc = b.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
