SELECT ANY_VALUE(jgbh) AS jgbh, ANY_VALUE(a.jgmc) AS jgmc, SUM(xzxms) AS xm, ROUND(SUM(xzxm_zjf),2) AS jf FROM v_stat_most_project AS a, v_ref_org_type AS b
a.jgmc = b.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
