mapper.select:
SELECT jgbh, jgmc, nf, ROUND(srhj/10000,2) AS zsr from stat_arp_fin_income
nf >= #{startYear} AND nf <= #{endYear}
order by jgbh ASC, nf ASC

SELECT a.jgbh, a.jgmc, d.research_field, a.income, b.outcome, c.rs FROM ( SELECT jgbh, jgmc, ROUND(srhj/10000,2) AS income FROM stat_arp_fin_income
nf = #{query.year}
) AS a, ( SELECT jgbh, jgmc, ROUND(zchj/10000,2) AS outcome FROM stat_arp_fin_expand
nf = #{query.year}
) AS b, ( SELECT jgbh, jgmc, zs AS rs FROM stat_arp_staff_duty
nf = #{query.year}
) AS c, ref_org_type AS d
a.jgbh=b.jgbh AND b.jgbh=c.jgbh AND c.jgbh=d.jgbh
AND a.JGBH in (
#{item}
)
AND a.JGBH is not null
AND research_field IS NOT NULL AND research_field != "NA" AND rs!=0
ORDER BY jgbh

SELECT ANY_VALUE(research_field) AS research_field, SUM(income) AS income, SUM(outcome) AS outcome FROM ( SELECT jgbh, jgmc, ROUND(srhj/10000,2) AS income FROM stat_arp_fin_income
nf = #{query.year}
) AS a, ( SELECT jgbh, jgmc, ROUND(zchj/10000,2) AS outcome FROM stat_arp_fin_expand
nf = #{query.year}
) AS b, ( SELECT jgbh, jgmc, zs AS rs FROM stat_arp_staff_duty
nf = #{query.year}
) AS c, ref_org_type AS d
a.jgbh=b.jgbh AND b.jgbh=c.jgbh AND c.jgbh=d.jgbh
AND a.JGBH in (
#{item}
)
AND a.JGBH is not null
AND research_field IS NOT NULL AND research_field != "NA" AND rs!=0
GROUP BY research_field ORDER BY jgbh

