LEFT(YEAR, 4) = #{query.year}
) AS a, ( SELECT JGBH, ROUND(ZCJE/10000,2) AS outcome, ZGZS AS rs FROM cas_no1_irc_pm_15
LEFT(YEAR, 4) = #{query.year}
) AS b, ref_org_type AS c
a.JGBH=b.JGBH AND b.JGBH=c.jgbh
AND a.JGBH in (
#{item}
)
AND a.JGBH is not null
AND research_field IS NOT NULL AND research_field != "NA" AND rs!=0
ORDER BY JGBH

