SELECT ANY_VALUE(nf) AS nf, SUM(zs) AS zs, SUM(zyjsry) AS zyjsry, SUM(xzglry) AS xzglry, SUM(gr) AS gr FROM stat_arp_staff_duty
1 = 1 AND nf >= #{query.startYear} AND nf <= #{query.endYear}
AND jgbh in (
#{item}
)
GROUP BY nf

SELECT a.jgbh, a.jgmc, a.field, a.numbers, a.funds, b.staff FROM ( SELECT jgbh, ROUND(SUM(ZS)/(#{query.endYear} - #{query.startYear} + 1), 0) as staff FROM stat_arp_staff_duty
1 = 1 AND nf >= #{query.startYear} AND nf <= #{query.endYear}
AND jgbh in (
#{item}
)
AND jgbh IS NOT NULL AND jgbh !=''
GROUP BY jgbh ) AS b, ( SELECT ANY_VALUE(r.jgbh) AS jgbh, ANY_VALUE(a.jgmc) AS jgmc, ANY_VALUE(r.research_field) AS field, SUM(a.xzxms) AS numbers, ROUND(SUM(a.xzxm_zjf), 2) AS funds FROM stat_nsfc_project as a, ref_org_type as r
a.jgmc = r.jgmc AND nf >= #{query.startYear} AND nf <= #{query.endYear}
AND jgbh in (
#{item}
)
AND jgbh IS NOT NULL AND jgbh !=''
AND research_field in (
#{item}
)
AND research_field is not null and research_field!='NA'
GROUP BY jgbh ) AS a
a.jgbh=b.jgbh AND b.staff != 0
ORDER BY a.jgbh

