mapper.select:
SELECT
  total, nf, country_ch
from
v_stat_hcauthors_count

  nf >= #{startYear}
  AND nf <= #{endYear}

SELECT SUM(total) AS contotal, country_ch from v_stat_hcauthors_count
nf >= #{startYear} AND nf <= #{endYear}
GROUP BY country_ch ORDER BY contotal DESC limit 0,5

SELECT SUM(total) AS contotal, country_ch from v_stat_hcauthors_count
nf <= #{endYear}
GROUP BY country_ch ORDER BY contotal DESC limit 0,5
