SELECT ANY_VALUE(a.nf) AS year, ANY_VALUE(a.jgbh) AS id, ANY_VALUE(a.jgmc) AS affiliation, SUM(a.bdrs) AS bd, SUM(a.bsrs) AS bs, SUM(sdrs) AS sd, SUM(ssrs) AS ss FROM stat_teacher_student AS a, ref_org_type AS b
a.jgbh=b.jgbh AND nf >= #{query.startYear} AND nf <= #{query.endYear}
AND b.research_field in (
#{item}
)
AND research_field is not null and research_field!='NA'
AND b.JGBH in (
#{item}
)
AND b.JGBH is not null
GROUP BY a.nf, a.jgmc ORDER BY a.jgmc, a.nf
