SELECT t1.nf AS year, nsfc_number, ROUND(nsfc_funds, 2) AS nsfc_funds, nsfc_rs, keyplan_number, ROUND(keyplan_funds, 2) AS keyplan_funds, ROUND(keyplan_funds_zycz, 2) AS keyplan_funds_zycz, ROUND(keyplan_funds_self, 2) AS keyplan_funds_self, keyplan_rs FROM ( SELECT pznd AS nf, COUNT(xmpzh) AS nsfc_number, SUM(pzje) AS nsfc_funds, COUNT(DISTINCT xm) AS nsfc_rs FROM temp_cas_staff_for_rencaicount AS a, cas_icp_isisn_nsfc_projects AS b, ref_org_type AS c
a.xm=b.xmfzr AND a.jgmc=b.ytdw AND a.jgbh=c.jgbh
AND c.JGBH in (
#{item}
)
AND c.JGBH is not null
AND c.research_field in (
#{item}
)
AND research_field is not null and research_field!='NA'
AND pznd >= #{query.startYear} AND pznd <= #{query.endYear}
GROUP BY pznd ) AS t1 LEFT JOIN ( SELECT LEFT(b.xmbh,4) AS nf, COUNT(xmbh) AS keyplan_number, SUM(xmzjf) AS keyplan_funds, SUM(zyczjf) AS keyplan_funds_zycz, (SUM(xmzjf)-SUM(zyczjf)) AS keyplan_funds_self, COUNT(DISTINCT xm) AS keyplan_rs FROM temp_cas_staff_for_rencaicount AS a, cas_national_key_r_d_plan AS b, ref_org_type AS c
a.xm=b.xmfzr AND a.jgmc=b.xmqtcddw AND a.jgbh=c.jgbh AND b.xmzjf IS NOT NULL AND b.xmzjf!='*' AND b.zyczjf IS NOT NULL AND b.zyczjf!='*'
AND c.JGBH in (
#{item}
)
AND c.JGBH is not null
AND c.research_field in (
#{item}
)
AND research_field is not null and research_field!='NA'
AND LEFT(b.xmbh, 4)>=${query.startYear} AND LEFT(b.xmbh,4)<=${query.endYear}
GROUP BY LEFT(b.xmbh,4) ) AS t2 ON t1.nf = t2.nf

